con:config.script:
import groovy.sql.Sql
import groovy.sql.GroovyRowResult

// Check whether TestCase is not running under Test Load.
if (context.LoadTestRunner == null) {
	log.debug "Checking the database content."

    String db_driver = context.expand( '${#TestSuite#db_driver}' )
    String db_url = context.expand( '${#TestSuite#db_url}' )
    String db_user = context.expand( '${#TestSuite#db_user}' )
    String db_passwd = context.expand( '${#TestSuite#db_passwd}' )
    sql = Sql.newInstance("$db_url", "$db_user", "$db_passwd", "$db_driver")

	String stat_name = testRunner.testCase.getPropertyValue( "stat_name" )
	String stat_value = testRunner.testCase.getPropertyValue( "stat_value" )
	def res_height = testRunner.testCase.getPropertyValue( "stat_height" )
	def res_width = testRunner.testCase.getPropertyValue( "stat_width" )
	String sqlQuery = "select count(*) from resolution as res, statistic as stat where res.id = stat.resolution_id and stat.name = '$stat_name' and stat.value = '$stat_value' AND res.height = $res_height AND res.width = $res_width"

	GroovyRowResult result = sql.firstRow(sqlQuery)
	Integer count = result.getAt(0)
	sql.close()

	if (count == null || count == 0) {
		testRunner.fail("No rows in database, the count is equal: '" + count + "'.")
	}
	else if (count > 1) {
	   testRunner.fail("More than one expected row in database, the count is equal: '" + count + "'.")
	}

	log.info "Database checked. OK"
}

con:testCase.con:setupScript:
import com.eviware.soapui.support.GroovyUtils import groovy.sql.Sql
log.debug "Clearing database for 'Local db' connection before TestCase run."
// Check whether TestCase is not running under Test Load. // If it is running under Test Load then check whether it is first execution. if (context.LoadTestRunner == null || context.TotalRunCount == 1) { String db_driver = context.expand( '${#TestSuite#db_driver}' ) String db_url = context.expand( '${#TestSuite#db_url}' ) String db_user = context.expand( '${#TestSuite#db_user}' ) String db_passwd = context.expand( '${#TestSuite#db_passwd}' )
GroovyUtils.registerJdbcDriver("$db_driver") sql = Sql.newInstance("$db_url", "$db_user", "$db_passwd", "$db_driver")
sql.execute("DELETE FROM statistic") sql.execute("DELETE FROM resolution") sql.close()
log.info "DB cleared." }
